import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';

function App() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState<string[] | null>(null);

  const handleSearch = useCallback(async () => {
    try {
      const response = await axios.post('http://localhost:3000/search', { query });
      console.log('Response from server:', response.data);
      if (!response.data.exists) {
        console.log('Data not found in Redis');
        setResults(null);
        return;
      }
      setResults(response.data.results);
    } catch (error) {
      console.error('Error:', error);
    }
  }, [query]); // Include query in the dependency array

  const handleKeyPress = (event: React.KeyboardEvent<HTMLInputElement>) => {
    setQuery(event.currentTarget.value);
  };

  useEffect(() => {
    handleSearch(); // Include handleSearch in the dependency array to satisfy ESLint
  }, [handleSearch]);

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-300 via-blue-200 to-blue-100">
      {/* ... rest of the code remains unchanged */}
    </div>
  );
}

export default App;
