import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState<string[] | null>(null);

  // Function to handle the search button click
  const handleSearch = async () => {
    try {
      // Send a POST request to the backend with the search query
      const response = await axios.post('http://localhost:3000/search', { query });
      console.log('Response: ',response.data);
      // Update the state with the search results received from the backend
      setResults(response.data.results);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div>
      <h1>Simple Search App</h1>
      <input
        type="text"
        placeholder="Enter your search query"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
      />
      {/* Call the handleSearch function when the button is clicked */}
      <button onClick={handleSearch}>Search</button>
      <div>
        <h2>Search Results:</h2>
        <ul>
        {results ? (
        <div>
          {results.map((result, index) => (
            <div key={index}>{result}</div>
          ))}
        </div>
      ) : (
        <p>No results available</p>
      )}
        </ul>
      </div>
    </div>
  );
}

export default App;
